import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Alert,
  CircularProgress,
  Card,
  CardContent,
  Chip,
  IconButton
} from '@mui/material';
import {
  Add as AddIcon,
  Save as SaveIcon,
  Clear as ClearIcon
} from '@mui/icons-material';
import { useNavigate, useLocation } from 'react-router-dom';
import { orderService, clientService, deviceService, serviceService } from '../services/api';

const NewOrder = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Данные для форм
  const [clients, setClients] = useState([]);
  const [devices, setDevices] = useState([]);
  const [services, setServices] = useState([]);
  const [deviceTypes, setDeviceTypes] = useState([]);
  const [brands, setBrands] = useState([]);

  // Форма заказа
  const [formData, setFormData] = useState({
    client_id: '',
    device_id: '',
    problem_description: '',
    services: []
  });

  // Форма нового клиента
  const [newClient, setNewClient] = useState({
    name: '',
    phone: '',
    email: '',
    address: ''
  });

  // Форма нового устройства
  const [newDevice, setNewDevice] = useState({
    id_type: '',
    id_brand: '',
    model: '',
    serial_number: ''
  });

  const [showNewClient, setShowNewClient] = useState(false);
  const [showNewDevice, setShowNewDevice] = useState(false);

  // Данные из заявки (если переходим из заявки)
  const [fromRequest, setFromRequest] = useState(null);

  useEffect(() => {
    loadInitialData();
    
    // Проверяем, перешли ли мы из заявки
    if (location.state?.fromRequest) {
      const request = location.state.fromRequest;
      setFromRequest(request);
      prefillFormFromRequest(request);
    }
  }, [location]);

  const prefillFormFromRequest = (request) => {
    console.log('Prefilling form from request:', request);
    
    // Создаем временного клиента из заявки
    const tempClient = {
      id_client: request.Client?.id_client,
      name: request.Client?.name,
      phone: request.contact_phone,
      email: request.contact_email || request.Client?.email
    };

    // Создаем временное устройство из заявки
    const tempDevice = {
      id_device: 'temp', // Временный ID
      model: request.device_model,
      Brand: { name: request.device_brand },
      DeviceType: { name: request.device_type }
    };

    // Добавляем в списки
    setClients(prev => [...prev, tempClient]);
    setDevices(prev => [...prev, tempDevice]);

    // Заполняем форму
    setFormData({
      client_id: tempClient.id_client,
      device_id: tempDevice.id_device,
      problem_description: request.problem_description,
      services: []
    });

    setSuccess('Данные из заявки автоматически заполнены!');
  };

  const loadInitialData = async () => {
    try {
      setLoading(true);
      const [clientsRes, devicesRes, servicesRes, typesRes, brandsRes] = await Promise.all([
        clientService.getClients({ page: 1, limit: 100 }),
        deviceService.getDevices({ page: 1, limit: 100 }),
        serviceService.getServices({ page: 1, limit: 100 }),
        deviceService.getDeviceTypes(),
        deviceService.getBrands()
      ]);

      setClients(clientsRes.data.clients || []);
      setDevices(devicesRes.data.devices || []);
      setServices(servicesRes.data.services || []);
      setDeviceTypes(typesRes.data || []);
      setBrands(brandsRes.data || []);
    } catch (error) {
      console.error('Error loading initial data:', error);
      setError('Ошибка при загрузке данных');
    } finally {
      setLoading(false);
    }
  };

  const handleFormChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleServiceToggle = (serviceId) => {
    setFormData(prev => {
      const services = [...prev.services];
      const index = services.indexOf(serviceId);
      
      if (index > -1) {
        services.splice(index, 1);
      } else {
        services.push(serviceId);
      }
      
      return { ...prev, services };
    });
  };

  const handleNewClientChange = (field, value) => {
    setNewClient(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleNewDeviceChange = (field, value) => {
    setNewDevice(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const createNewClient = async () => {
    try {
      const response = await clientService.createClient(newClient);
      setClients(prev => [...prev, response.data]);
      setFormData(prev => ({ ...prev, client_id: response.data.id_client }));
      setShowNewClient(false);
      setNewClient({ name: '', phone: '', email: '', address: '' });
    } catch (error) {
      setError('Ошибка при создании клиента');
    }
  };

  const createNewDevice = async () => {
    try {
      const response = await deviceService.createDevice(newDevice);
      setDevices(prev => [...prev, response.data]);
      setFormData(prev => ({ ...prev, device_id: response.data.id_device }));
      setShowNewDevice(false);
      setNewDevice({ id_type: '', id_brand: '', model: '', serial_number: '' });
    } catch (error) {
      setError('Ошибка при создании устройства');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.client_id || !formData.device_id || !formData.problem_description) {
      setError('Заполните обязательные поля: клиент, устройство и описание проблемы');
      return;
    }

    // Если устройство временное (из заявки), нужно создать настоящее устройство
    let finalDeviceId = formData.device_id;
    if (formData.device_id === 'temp' && fromRequest) {
      try {
        // Создаем устройство на основе данных из заявки
        const deviceResponse = await deviceService.createDevice({
          id_type: deviceTypes.find(t => t.name === fromRequest.device_type)?.id_type || 1,
          id_brand: brands.find(b => b.name === fromRequest.device_brand)?.id_brand || 1,
          model: fromRequest.device_model,
          serial_number: ''
        });
        finalDeviceId = deviceResponse.data.id_device;
        setDevices(prev => [...prev, deviceResponse.data]);
      } catch (error) {
        setError('Ошибка при создании устройства из заявки');
        return;
      }
    }

    try {
      setSubmitting(true);
      setError('');
      
      const orderData = {
        ...formData,
        device_id: finalDeviceId
      };
      
      await orderService.createOrder(orderData);
      
      setSuccess('Заказ успешно создан!');
      setTimeout(() => {
        navigate('/orders');
      }, 2000);
      
    } catch (error) {
      setError(error.response?.data?.error || 'Ошибка при создании заказа');
    } finally {
      setSubmitting(false);
    }
  };

  const getSelectedServices = () => {
    return services.filter(service => formData.services.includes(service.id_service));
  };

  const getTotalPrice = () => {
    return getSelectedServices().reduce((total, service) => total + parseFloat(service.price), 0);
  };

  const clearFromRequest = () => {
    setFromRequest(null);
    setFormData({
      client_id: '',
      device_id: '',
      problem_description: '',
      services: []
    });
  };

  if (loading) {
    return (
      <Container>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress />
        </Box>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ mb: 3, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="h4" component="h1">
          Новый заказ на ремонт
          {fromRequest && (
            <Chip 
              label="Создается из заявки" 
              color="primary" 
              size="small" 
              sx={{ ml: 2 }}
            />
          )}
        </Typography>
        <Typography variant="h4" component="h1">
        {fromRequest && (
          <Button
            variant="outlined"
            startIcon={<ClearIcon />}
            onClick={clearFromRequest}
          >
            Очистить данные заявки
          </Button>
        )}
      </Typography>
      </Box>

      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
      {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}

      {/* Блок информации из заявки */}
      {fromRequest && (
        <Card sx={{ mb: 3, bgcolor: 'primary.light', color: 'white' }}>
          <CardContent>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Box>
                <Typography variant="h6" gutterBottom>
                  Данные из заявки #{fromRequest.id_request}
                </Typography>
                <Typography variant="body2">
                  Клиент: {fromRequest.Client?.name} | 
                  Устройство: {fromRequest.device_brand} {fromRequest.device_model} | 
                  Тип: {fromRequest.device_type}
                </Typography>
              </Box>
            </Box>
          </CardContent>
        </Card>
      )}

      <Paper sx={{ p: 3 }}>
        <Box component="form" onSubmit={handleSubmit}>
          <Grid container spacing={3}>
            {/* Выбор клиента */}
            <Grid item xs={12}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
                <FormControl fullWidth>
                  <InputLabel>Клиент *</InputLabel>
                  <Select
                    value={formData.client_id}
                    label="Клиент *"
                    onChange={(e) => handleFormChange('client_id', e.target.value)}
                  >
                    {clients.map(client => (
                      <MenuItem key={client.id_client} value={client.id_client}>
                        {client.name} - {client.phone}
                        {client.id_client === 'temp' && ' (из заявки)'}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <Button
                  variant="outlined"
                  onClick={() => setShowNewClient(!showNewClient)}
                  startIcon={<AddIcon />}
                >
                  Новый клиент
                </Button>
              </Box>

              {showNewClient && (
                <Card variant="outlined" sx={{ mb: 2 }}>
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      Новый клиент
                    </Typography>
                    <Grid container spacing={2}>
                      <Grid item xs={12} sm={6}>
                        <TextField
                          fullWidth
                          label="Имя *"
                          value={newClient.name}
                          onChange={(e) => handleNewClientChange('name', e.target.value)}
                        />
                      </Grid>
                      <Grid item xs={12} sm={6}>
                        <TextField
                          fullWidth
                          label="Телефон *"
                          value={newClient.phone}
                          onChange={(e) => handleNewClientChange('phone', e.target.value)}
                        />
                      </Grid>
                      <Grid item xs={12} sm={6}>
                        <TextField
                          fullWidth
                          label="Email"
                          type="email"
                          value={newClient.email}
                          onChange={(e) => handleNewClientChange('email', e.target.value)}
                        />
                      </Grid>
                      <Grid item xs={12} sm={6}>
                        <TextField
                          fullWidth
                          label="Адрес"
                          value={newClient.address}
                          onChange={(e) => handleNewClientChange('address', e.target.value)}
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <Button
                          variant="contained"
                          onClick={createNewClient}
                          disabled={!newClient.name || !newClient.phone}
                        >
                          Создать клиента
                        </Button>
                      </Grid>
                    </Grid>
                  </CardContent>
                </Card>
              )}
            </Grid>

            {/* Выбор устройства */}
            <Grid item xs={12}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
                <FormControl fullWidth>
                  <InputLabel>Устройство *</InputLabel>
                  <Select
                    value={formData.device_id}
                    label="Устройство *"
                    onChange={(e) => handleFormChange('device_id', e.target.value)}
                  >
                    {devices.map(device => (
                      <MenuItem key={device.id_device} value={device.id_device}>
                        {device.Brand?.name} {device.model} ({device.DeviceType?.name})
                        {device.id_device === 'temp' && ' (из заявки)'}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <Button
                  variant="outlined"
                  onClick={() => setShowNewDevice(!showNewDevice)}
                  startIcon={<AddIcon />}
                >
                  Новое устройство
                </Button>
              </Box>

              {showNewDevice && (
                <Card variant="outlined" sx={{ mb: 2 }}>
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      Новое устройство
                    </Typography>
                    <Grid container spacing={2}>
                      <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                          <InputLabel>Тип устройства *</InputLabel>
                          <Select
                            value={newDevice.id_type}
                            label="Тип устройства *"
                            onChange={(e) => handleNewDeviceChange('id_type', e.target.value)}
                          >
                            {deviceTypes.map(type => (
                              <MenuItem key={type.id_type} value={type.id_type}>
                                {type.name}
                              </MenuItem>
                            ))}
                          </Select>
                        </FormControl>
                      </Grid>
                      <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                          <InputLabel>Бренд *</InputLabel>
                          <Select
                            value={newDevice.id_brand}
                            label="Бренд *"
                            onChange={(e) => handleNewDeviceChange('id_brand', e.target.value)}
                          >
                            {brands.map(brand => (
                              <MenuItem key={brand.id_brand} value={brand.id_brand}>
                                {brand.name}
                              </MenuItem>
                            ))}
                          </Select>
                        </FormControl>
                      </Grid>
                      <Grid item xs={12} sm={6}>
                        <TextField
                          fullWidth
                          label="Модель *"
                          value={newDevice.model}
                          onChange={(e) => handleNewDeviceChange('model', e.target.value)}
                        />
                      </Grid>
                      <Grid item xs={12} sm={6}>
                        <TextField
                          fullWidth
                          label="Серийный номер"
                          value={newDevice.serial_number}
                          onChange={(e) => handleNewDeviceChange('serial_number', e.target.value)}
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <Button
                          variant="contained"
                          onClick={createNewDevice}
                          disabled={!newDevice.id_type || !newDevice.id_brand || !newDevice.model}
                        >
                          Создать устройство
                        </Button>
                      </Grid>
                    </Grid>
                  </CardContent>
                </Card>
              )}
            </Grid>

            {/* Описание проблемы */}
            <Grid item xs={12}>
              <TextField
                fullWidth
                multiline
                rows={4}
                label="Описание проблемы *"
                value={formData.problem_description}
                onChange={(e) => handleFormChange('problem_description', e.target.value)}
              />
            </Grid>

            {/* Выбор услуг */}
            <Grid item xs={12}>
              <Typography variant="h6" gutterBottom>
                Услуги
              </Typography>
              <Grid container spacing={2}>
                {services.map(service => (
                  <Grid item xs={12} sm={6} md={4} key={service.id_service}>
                    <Card 
                      variant={formData.services.includes(service.id_service) ? "elevated" : "outlined"}
                      sx={{ 
                        cursor: 'pointer',
                        border: formData.services.includes(service.id_service) ? '2px solid' : '1px solid',
                        borderColor: formData.services.includes(service.id_service) ? 'primary.main' : 'divider'
                      }}
                      onClick={() => handleServiceToggle(service.id_service)}
                    >
                      <CardContent>
                        <Typography variant="subtitle1" gutterBottom>
                          {service.name}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" gutterBottom>
                          {service.description}
                        </Typography>
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                          <Typography variant="h6" color="primary">
                            {service.price} ₽
                          </Typography>
                          <Chip 
                            label={formData.services.includes(service.id_service) ? "Выбрано" : "Выбрать"}
                            color={formData.services.includes(service.id_service) ? "primary" : "default"}
                            size="small"
                          />
                        </Box>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Grid>

            {/* Итоговая стоимость */}
            {formData.services.length > 0 && (
              <Grid item xs={12}>
                <Card sx={{ bgcolor: 'primary.light', color: 'white' }}>
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      Предварительная стоимость
                    </Typography>
                    <Typography variant="h4">
                      {getTotalPrice()} ₽
                    </Typography>
                    <Typography variant="body2">
                      Выбрано услуг: {formData.services.length}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            )}

            {/* Кнопки отправки */}
            <Grid item xs={12}>
              <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
                <Button
                  variant="outlined"
                  onClick={() => navigate('/orders')}
                >
                  Отмена
                </Button>
                <Button
                  type="submit"
                  variant="contained"
                  disabled={submitting}
                  startIcon={submitting ? <CircularProgress size={20} /> : <SaveIcon />}
                >
                  {submitting ? 'Создание...' : 'Создать заказ'}
                </Button>
              </Box>
            </Grid>
          </Grid>
        </Box>
      </Paper>
    </Container>
  );
};

export default NewOrder;
