import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  Chip,
  IconButton,
  Typography,
  Box,
  Button,
  TextField,
  InputAdornment,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  CircularProgress
} from '@mui/material';
import {
  Visibility as ViewIcon,
  Add as AddIcon,
  Search as SearchIcon
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { orderService } from '../services/api';
import { format } from 'date-fns';
import { ru } from 'date-fns/locale';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [totalCount, setTotalCount] = useState(0);
  const [search, setSearch] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const navigate = useNavigate();

  const statusColors = {
    'accepted': 'default', // Принят
    'diagnostics': 'primary', // Диагностика
    'approval': 'warning', // Согласование
    'in_progress': 'info', // В ремонте
    'completed': 'success', // Готов
    'issued': 'secondary', // Выдан
    'cancelled': 'error'    // Отменен
  };

  const statusLabels = {
    'accepted': 'Принят',
    'diagnostics': 'Диагностика',
    'approval': 'Согласование',
    'in_progress': 'В ремонте',
    'completed': 'Готов',
    'issued': 'Выдан',
    'cancelled': 'Отменен'
  };

  const loadOrders = async () => {
    try {
      setLoading(true);
      const params = {
        page: page + 1,
        limit: rowsPerPage
      };
      
      if (search) {
        params.search = search;
      }
      
      if (statusFilter) {
        params.status = statusFilter;
      }

      console.log('Loading orders with params:', params);
      const response = await orderService.getOrders(params);
      console.log('Orders response:', response.data);
      
      setOrders(response.data.orders || response.data.data || []);
      setTotalCount(response.data.totalCount || response.data.total || 0);
    } catch (error) {
      console.error('Error loading orders:', error);
      setOrders([]);
      setTotalCount(0);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadOrders();
  }, [page, rowsPerPage, statusFilter]);

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    setPage(0);
    loadOrders();
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleStatusFilterChange = (event) => {
    setStatusFilter(event.target.value);
    setPage(0);
  };

  // Функция для получения имени клиента
  const getClientName = (order) => {
    if (order.client) {
      return order.client.name || order.client.full_name || 'Клиент';
    }
    if (order.Client) {
      return order.Client.name || order.Client.full_name || 'Клиент';
    }
    if (order.client_name) {
      return order.client_name;
    }
    return 'Клиент не указан';
  };

  // Функция для получения телефона клиента
  const getClientPhone = (order) => {
    if (order.client) {
      return order.client.phone;
    }
    if (order.Client) {
      return order.Client.phone;
    }
    if (order.client_phone) {
      return order.client_phone;
    }
    return '—';
  };

  // Функция для получения информации об устройстве
  const getDeviceInfo = (order) => {
    if (order.device) {
      const brand = order.device.brand || order.device.Brand?.name;
      const model = order.device.model;
      const type = order.device.type || order.device.DeviceType?.name;
      return { brand, model, type };
    }
    if (order.Device) {
      const brand = order.Device.brand || order.Device.Brand?.name;
      const model = order.Device.model;
      const type = order.Device.type || order.Device.DeviceType?.name;
      return { brand, model, type };
    }
    if (order.device_brand || order.device_model) {
      return {
        brand: order.device_brand,
        model: order.device_model,
        type: order.device_type
      };
    }
    return { brand: '—', model: '—', type: '—' };
  };

  // Функция для получения стоимости
  const getOrderCost = (order) => {
    if (order.final_cost !== undefined && order.final_cost !== null) {
      return order.final_cost;
    }
    if (order.cost_estimate !== undefined && order.cost_estimate !== null) {
      return order.cost_estimate;
    }
    if (order.total_cost !== undefined && order.total_cost !== null) {
      return order.total_cost;
    }
    return null;
  };

  // Функция для получения статуса
  const getOrderStatus = (order) => {
    if (order.status) {
      return order.status;
    }
    if (order.id_status) {
      // Конвертируем числовой статус в строковый
      const statusMap = {
        1: 'accepted',
        2: 'diagnostics',
        3: 'approval',
        4: 'in_progress',
        5: 'completed',
        6: 'issued',
        7: 'cancelled'
      };
      return statusMap[order.id_status] || 'accepted';
    }
    if (order.order_status) {
      return order.order_status;
    }
    return 'accepted';
  };

  // Функция для получения даты создания
  const getOrderDate = (order) => {
    if (order.date_created) {
      return order.date_created;
    }
    if (order.created_at) {
      return order.created_at;
    }
    if (order.createdAt) {
      return order.createdAt;
    }
    return new Date();
  };

  // Функция для получения мастера
  const getMasterName = (order) => {
    if (order.master) {
      return order.master.name || order.master.full_name || 'Не назначен';
    }
    if (order.Master) {
      return order.Master.name || order.Master.full_name || 'Не назначен';
    }
    if (order.master_name) {
      return order.master_name;
    }
    return 'Не назначен';
  };

  return (
    <Container maxWidth="xl">
      <Box sx={{ mb: 3, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="h4" component="h1">
          Заказы на ремонт
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={() => navigate('/orders/new')}
        >
          Новый заказ
        </Button>
      </Box>

      {/* Фильтры и поиск */}
      <Paper sx={{ p: 2, mb: 2 }}>
        <Box component="form" onSubmit={handleSearchSubmit} sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
          <TextField
            placeholder="Поиск по клиентам или устройствам..."
            value={search}
            onChange={handleSearch}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
            sx={{ flexGrow: 1 }}
          />
          <Button type="submit" variant="outlined">
            Найти
          </Button>
          <FormControl sx={{ minWidth: 150 }}>
            <InputLabel>Статус</InputLabel>
            <Select
              value={statusFilter}
              label="Статус"
              onChange={handleStatusFilterChange}
            >
              <MenuItem value="">Все</MenuItem>
              <MenuItem value="accepted">Принят</MenuItem>
              <MenuItem value="diagnostics">Диагностика</MenuItem>
              <MenuItem value="approval">Согласование</MenuItem>
              <MenuItem value="in_progress">В ремонте</MenuItem>
              <MenuItem value="completed">Готов</MenuItem>
              <MenuItem value="issued">Выдан</MenuItem>
              <MenuItem value="cancelled">Отменен</MenuItem>
            </Select>
          </FormControl>
        </Box>
      </Paper>

      <Paper>
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>№</TableCell>
                <TableCell>Клиент</TableCell>
                <TableCell>Устройство</TableCell>
                <TableCell>Проблема</TableCell>
                <TableCell>Статус</TableCell>
                <TableCell>Мастер</TableCell>
                <TableCell>Дата создания</TableCell>
                <TableCell>Стоимость</TableCell>
                <TableCell>Действия</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {loading ? (
                <TableRow>
                  <TableCell colSpan={9} align="center" sx={{ py: 4 }}>
                    <CircularProgress />
                  </TableCell>
                </TableRow>
              ) : (
                <>
                  {orders.map((order) => {
                    const clientName = getClientName(order);
                    const clientPhone = getClientPhone(order);
                    const deviceInfo = getDeviceInfo(order);
                    const orderCost = getOrderCost(order);
                    const orderStatus = getOrderStatus(order);
                    const orderDate = getOrderDate(order);
                    const masterName = getMasterName(order);

                    return (
                      <TableRow key={order.id_order || order.id} hover>
                        <TableCell>#{order.id_order || order.id}</TableCell>
                        <TableCell>
                          <Box>
                            <Typography variant="body2" fontWeight="bold">
                              {clientName}
                            </Typography>
                            <Typography variant="body2" color="textSecondary">
                              {clientPhone}
                            </Typography>
                          </Box>
                        </TableCell>
                        <TableCell>
                          <Box>
                            <Typography variant="body2" fontWeight="bold">
                              {deviceInfo.brand} {deviceInfo.model}
                            </Typography>
                            <Typography variant="body2" color="textSecondary">
                              {deviceInfo.type}
                            </Typography>
                          </Box>
                        </TableCell>
                        <TableCell>
                          <Typography 
                            variant="body2" 
                            sx={{ 
                              maxWidth: 200,
                              overflow: 'hidden',
                              textOverflow: 'ellipsis',
                              whiteSpace: 'nowrap'
                            }}
                          >
                            {order.problem_description || order.description || 'Описание не указано'}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Chip
                            label={statusLabels[orderStatus] || 'Неизвестно'}
                            color={statusColors[orderStatus] || 'default'}
                            size="small"
                          />
                        </TableCell>
                        <TableCell>
                          {masterName}
                        </TableCell>
                        <TableCell>
                          {format(new Date(orderDate), 'dd.MM.yyyy', { locale: ru })}
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2" fontWeight="bold">
                            {orderCost ? `${orderCost} ₽` : '—'}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <IconButton
                            color="primary"
                            onClick={() => navigate(`/orders/${order.id_order || order.id}`)}
                            title="Просмотреть детали"
                          >
                            <ViewIcon />
                          </IconButton>
                        </TableCell>
                      </TableRow>
                    );
                  })}
                  {orders.length === 0 && (
                    <TableRow>
                      <TableCell colSpan={9} align="center" sx={{ py: 4 }}>
                        <Typography color="textSecondary">
                          {search || statusFilter ? 'Заказы не найдены' : 'Нет заказов'}
                        </Typography>
                      </TableCell>
                    </TableRow>
                  )}
                </>
              )}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={totalCount}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
          labelRowsPerPage="Строк на странице:"
          labelDisplayedRows={({ from, to, count }) => 
            `${from}-${to} из ${count}`
          }
        />
      </Paper>
    </Container>
  );
};

export default Orders;
