import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Orders from './pages/Orders';
import NewOrder from './pages/NewOrder';
import OrderDetail from './pages/OrderDetail';
import Clients from './pages/Clients';
import Devices from './pages/Devices';
import Services from './pages/Services';
import Users from './pages/Users';
import Layout from './components/Layout';
import ClientLogin from './pages/ClientLogin';
import ClientRegister from './pages/ClientRegister';
import ClientDashboard from './pages/ClientDashboard';
import NewRepairRequest from './pages/NewRepairRequest';
import RepairRequests from './pages/RepairRequests';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
  },
});

// Защищенный маршрут для сотрудников
const ProtectedRoute = ({ children, requiredRole }) => {
  const { user } = useAuth();
  
  if (!user) {
    return <Navigate to="/login" />;
  }
  
  if (requiredRole && user.role !== requiredRole && user.role !== 'admin') {
    return <Navigate to="/" />;
  }
  
  return children;
};

// Защищенный маршрут для клиентов
const ClientProtectedRoute = ({ children }) => {
  const client = localStorage.getItem('client');
  const clientToken = localStorage.getItem('clientToken');
  
  if (!client || !clientToken) {
    return <Navigate to="/client-login" />;
  }
  
  return children;
};

// Компонент для проверки, авторизован ли пользователь как клиент
const ClientAuthCheck = () => {
  const client = localStorage.getItem('client');
  return client ? <Navigate to="/client/dashboard" /> : <Navigate to="/client-login" />;
};

// Компонент для проверки, авторизован ли пользователь как сотрудник
const EmployeeAuthCheck = () => {
  const { user } = useAuth();
  return user ? <Navigate to="/" /> : <Navigate to="/login" />;
};

const AppRoutes = () => {
  const { user } = useAuth();

  return (
    <Routes>
      {/* Публичные маршруты для клиентов */}
      <Route path="/client-login" element={<ClientLogin />} />
      <Route path="/client-register" element={<ClientRegister />} />
      
      {/* Защищенные маршруты для клиентов */}
      <Route path="/client/dashboard" element={
        <ClientProtectedRoute>
          <ClientDashboard />
        </ClientProtectedRoute>
      } />
      <Route path="/client/new-request" element={
        <ClientProtectedRoute>
          <NewRepairRequest />
        </ClientProtectedRoute>
      } />
      
      {/* Публичные маршруты для сотрудников */}
      <Route path="/login" element={<Login />} />
      
      {/* Защищенные маршруты для сотрудников */}
      <Route path="/" element={
        <ProtectedRoute>
          <Layout>
            <Dashboard />
          </Layout>
        </ProtectedRoute>
      } />
      <Route path="/orders" element={
        <ProtectedRoute>
          <Layout>
            <Orders />
          </Layout>
        </ProtectedRoute>
      } />
      <Route path="/orders/new" element={
        <ProtectedRoute>
          <Layout>
            <NewOrder />
          </Layout>
        </ProtectedRoute>
      } />
      <Route path="/orders/:id" element={
        <ProtectedRoute>
          <Layout>
            <OrderDetail />
          </Layout>
        </ProtectedRoute>
      } />
      <Route path="/clients" element={
        <ProtectedRoute>
          <Layout>
            <Clients />
          </Layout>
        </ProtectedRoute>
      } />
      <Route path="/devices" element={
        <ProtectedRoute>
          <Layout>
            <Devices />
          </Layout>
        </ProtectedRoute>
      } />
      <Route path="/services" element={
        <ProtectedRoute>
          <Layout>
            <Services />
          </Layout>
        </ProtectedRoute>
      } />
      <Route path="/repair-requests" element={
        <ProtectedRoute>
          <Layout>
            <RepairRequests />
          </Layout>
        </ProtectedRoute>
      } />
      
      {/* Админские маршруты */}
      {user?.role === 'admin' && (
        <Route path="/users" element={
          <ProtectedRoute requiredRole="admin">
            <Layout>
              <Users />
            </Layout>
          </ProtectedRoute>
        } />
      )}
      
      {/* Редиректы по умолчанию */}
      <Route path="/client" element={<ClientAuthCheck />} />
      <Route path="/employee" element={<EmployeeAuthCheck />} />
      
      {/* Fallback route */}
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
};

const App = () => {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AuthProvider>
        <Router>
          <AppRoutes />
        </Router>
      </AuthProvider>
    </ThemeProvider>
  );
};

export default App;
