import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Button,
  Card,
  CardContent,
  Chip,
  Grid,
  Alert
} from '@mui/material';
import { Add as AddIcon } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

const ClientDashboard = () => {
  const [client, setClient] = useState(null);
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const clientData = localStorage.getItem('client');
    if (clientData) {
      setClient(JSON.parse(clientData));
    }
    loadMyRequests();
  }, []);

  const loadMyRequests = async () => {
    try {
      const token = localStorage.getItem('clientToken');
      const response = await fetch('http://localhost:5000/api/repair-requests/my-requests', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setRequests(data);
      } else {
        setError('Ошибка загрузки заявок');
      }
    } catch (error) {
      setError('Ошибка соединения');
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'approved': return 'success';
      case 'rejected': return 'error';
      default: return 'warning';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'pending': return 'На рассмотрении';
      case 'approved': return 'Одобрена';
      case 'rejected': return 'Отклонена';
      default: return status;
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('clientToken');
    localStorage.removeItem('client');
    navigate('/client-login');
  };

  if (!client) {
    return (
      <Container>
        <Alert severity="error">Требуется авторизация</Alert>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ mb: 3, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Box>
          <Typography variant="h4" component="h1">
            Личный кабинет
          </Typography>
          <Typography variant="h6" color="textSecondary">
            Добро пожаловать, {client.name}!
          </Typography>
        </Box>
        <Box sx={{ display: 'flex', gap: 2 }}>
          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={() => navigate('/client/new-request')}
          >
            Новая заявка
          </Button>
          <Button variant="outlined" onClick={handleLogout}>
            Выйти
          </Button>
        </Box>
      </Box>

      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Ваши данные
            </Typography>
            <Typography variant="body2" color="textSecondary">
              <strong>Телефон:</strong> {client.phone}
            </Typography>
            <Typography variant="body2" color="textSecondary">
              <strong>Email:</strong> {client.email}
            </Typography>
            {client.address && (
              <Typography variant="body2" color="textSecondary">
                <strong>Адрес:</strong> {client.address}
              </Typography>
            )}
          </Paper>
        </Grid>

        <Grid item xs={12} md={8}>
          <Typography variant="h6" gutterBottom>
            Мои заявки на ремонт
          </Typography>

          {requests.length === 0 ? (
            <Paper sx={{ p: 3, textAlign: 'center' }}>
              <Typography color="textSecondary">
                У вас пока нет заявок на ремонт
              </Typography>
              <Button
                variant="contained"
                startIcon={<AddIcon />}
                onClick={() => navigate('/client/new-request')}
                sx={{ mt: 2 }}
              >
                Создать первую заявку
              </Button>
            </Paper>
          ) : (
            requests.map((request) => (
              <Card key={request.id_request} sx={{ mb: 2 }}>
                <CardContent>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                    <Box>
                      <Typography variant="h6">
                        {request.device_brand} {request.device_model}
                      </Typography>
                      <Typography variant="body2" color="textSecondary">
                        {request.device_type}
                      </Typography>
                    </Box>
                    <Chip
                      label={getStatusText(request.status)}
                      color={getStatusColor(request.status)}
                    />
                  </Box>
                  
                  <Typography variant="body2" sx={{ mb: 2 }}>
                    {request.problem_description}
                  </Typography>
                  
                  <Typography variant="caption" color="textSecondary">
                    Создана: {new Date(request.created_at).toLocaleDateString('ru-RU')}
                  </Typography>
                </CardContent>
              </Card>
            ))
          )}
        </Grid>
      </Grid>
    </Container>
  );
};

export default ClientDashboard;
